Games = new Mongo.Collection('games');
var emptyImage = "data:image/png;base64,";
if (Meteor.isClient) {

  Tracker.autorun(function () {
    var games = Games.find({}, { sort: {started: -1} }).fetch();
    console.log('autorun', games);
    if (! _.isEmpty(games)) {
      var newestGameId = games[0]._id;
      console.log('new game started', newestGameId);
      Session.set('game', newestGameId);
    }
  });


  Template.registerHelper('emptyImage', function () {
    return emptyImage;
  });

  Template.registerHelper('absoluteUrl', function () {
    return Meteor.absoluteUrl();
  });

  Template.registerHelper('momo', function(aDate) {
    if (_.isDate(aDate)) {
      return moment(aDate).fromNow();
    }
    return '';
  });

  Template.registerHelper('game', function () {
    var game = Session.get('game');
    return game;
  });

  Template.registerHelper('games', function () {
    var games = Games.find({}, { sort: {started: -1} }).fetch();
    if (games) {
      return games;
    }
    return [];
  });

  //functions
  var clearCanvas = function clearCanvas (template) {
    var sketchCanvas = template.find(".sketch-canvas");
    $(sketchCanvas).sketch().actions = []; // this line empties the actions
    var context = sketchCanvas.getContext('2d');
    context.clearRect(0, 0, sketchCanvas.width, sketchCanvas.height);
  };

  Template.main.events({
    'click [data-action="host-mode"]': function (event) {
      event.preventDefault();
      Session.set('mode', 'host');
    },
    'click [data-action="play-mode"]': function (event) {
      event.preventDefault();
      Session.set('mode', 'play');
    },
    'click [data-action="new-game"]': function (event) {
      event.preventDefault();
      Meteor.call('newGame', function (error, result) {
        if (error) {
          throw error;
        } else {
          console.log('game set to: ', result);
          //Session.set('game', result);
          Session.set('mode', 'play');
        }
      });
    },
    'click [data-action="select-game"]': function (event) {
      event.preventDefault();
      Session.set('game', this._id);
      Session.set('mode', 'play');
    },
    'click [data-action="go-home"]': function (event) {
      event.preventDefault();
      Session.set('mode', null);
    }
  });

  Template.main.helpers({
    modeSelected: function () {
      var mode = Session.get('mode');
      return mode;
    },
    playMode: function () {
      var mode = Session.get('mode');
      return mode === 'play';
    },
    hostMode: function () {
      var mode = Session.get('mode');
      return mode === 'host';
    },
  });

  Template.sketchView.rendered = function () {
    $(this.find('.sketch-canvas')).sketch();
  };

  Template.sketchView.events({
    'click [data-action="send"]': function (event, template) {
      var sketch = $(template.find(".sketch-canvas")).sketch();
      var dataUrl = sketch.el.toDataURL();
      var gameId = Session.get('game');
      var $button = $(template.find('[data-action="send"]'));
      $button.attr('disabled', 'disabled');
      $button.toggleClass('btn-primary');
      $button.text("Sending...")
      Meteor.call('addSketch', gameId, dataUrl, function (error, result) {
        if (error) {
          throw error;
        } else {
          $button.text("Send")
          $button.removeAttr('disabled');
          $button.toggleClass('btn-primary');
        }
      });
      console.log(dataUrl);
    },
    'click [data-action="clear"]': function (event, template) {
      clearCanvas(template);
    },
  });

  Template.registerHelper('imageUrl', function() {
    var gameId = Session.get('game');
    var game = Games.findOne({_id: gameId});
    if (game) {
      var squiggleUrl = game.imageUrl;
      return squiggleUrl;
    }
    return;
  });

  Template.hostView.helpers({
    compBackgroundUrl: function () {
      var gameId = Session.get('game');
      var game = Games.findOne({_id: gameId});
      var lastImage = " url(" + game.imageUrl + ")";
      var result = '';
      if (game) {
        _.each(game.squiggles, function (squiggle) {
          result += "url(" + squiggle + "), ";
        });
      }
      result += lastImage;
      return result;
    },
    // submitted: function () {
    //   var gameId = Session.get('game');
    //   var game = Games.findOne({_id: gameId});
    //   var results = [];
    //   if (game) {
    //     _.each(game.squiggles, function (squiggle) {
    //       results.push({
    //         backgroundUrl: "url(" + squiggle + "), url(" + game.imageUrl + ")"
    //       });
    //     });
    //   }
    //   return results;
    // },
  });

}

if (Meteor.isServer) {
  chance = Chance();
  Meteor.methods({
    newGame: function () {
      var now = moment();
      //if there are more than 10 games, remove the oldest
      var doc = {
        //find a random squiggle url
        name: chance.name(),
        started: now.toDate(),
        imageUrl: "/squiggle_" + chance.integer({min: 1, max: 16}) + ".jpg",
        squiggles: [],
      };
      //create a new game
      var newGameId = Games.insert(doc);
      //return the game ID
      return newGameId;
    },
    addSketch: function (gameId, sketchDataUrl) {
      //var game = Games.findOne({_id: gameId});
      var updated = Games.update(
        {_id: gameId},
        {
          $push: {squiggles: sketchDataUrl}
        }
      );
      return updated;
    },
  });
}
